import React, { useState, useEffect } from 'react';
import axios from 'axios';

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlus, faTrash } from '@fortawesome/free-solid-svg-icons';

const TodoItem = ({ todo, onToggleComplete }) => {
    const { id, title, completed } = todo;

    const handleToggleComplete = () => {
        onToggleComplete(id, !completed);
    };

    return (
        <div>
            <input
                type="checkbox"
                checked={completed}
                onChange={handleToggleComplete}
            />
            <span>{title}</span>
        </div>
    );
};

const TodoList = () => {
    const [todos, setTodos] = useState([]);
    const [completedTodos, setCompletedTodos] = useState([]);

    const toggleCompleteTodo = (id, completed) => {
        const updatedTodos = todos.map((todo) => {
            if (todo.id === id) {
                return { ...todo, completed };
            }
            return todo;
        });

        setTodos(updatedTodos);

        if (completed) {
            const completedTodo = todos.find((todo) => todo.id === id);
            setCompletedTodos((prevCompletedTodos) => [...prevCompletedTodos, completedTodo]);
        } else {
            setCompletedTodos((prevCompletedTodos) =>
                prevCompletedTodos.filter((todo) => todo.id !== id)
            );
        }

        axios
            .put(`http://localhost:3001/api/items/complete/${id}`, { completed })
            .then(() => {
                console.log(`Задача ${id} обновлена`);
            })
            .catch((error) => {
                console.error('Ошибка при обновлении задачи:', error);
            });
    };

    return (
        <div>
            <h2>Активные задачи</h2>
            {todos.map((todo) => (
                <TodoItem key={todo.id} todo={todo} onToggleComplete={toggleCompleteTodo} />
            ))}

            <h2>Выполненные задачи</h2>
            {completedTodos.map((todo) => (
                <TodoItem key={todo.id} todo={todo} onToggleComplete={toggleCompleteTodo} />
            ))}
        </div>
    );
};


const AddTodo = ({ onAddTodo }) => {
    const [newTodo, setNewTodo] = useState('');

    const handleAddTodo = () => {
        axios
            .post('http://localhost:3001/api/items/add', {
                name: newTodo,
                completed: false,
            })
            .then((response) => {
                const data = response.data;
                console.log('New todo added:', data);
                setNewTodo('');
                onAddTodo(data);
            })
            .catch((error) => {
                console.error('Error adding new todo:', error);
            });
    };

    return (
        <div style={{ display: 'flex', alignItems: 'center', marginBottom: '5px' }}>
            <input
                type="text"
                placeholder="Введите новую заметку"
                value={newTodo}
                onChange={(e) => setNewTodo(e.target.value)}
                style={{ marginRight: '20px', marginLeft: '10px', fontSize: '1.5rem' }}
            />
            <button onClick={handleAddTodo} style={{ height: '2rem', width: '2rem' }}>
                <FontAwesomeIcon icon={faPlus} />
            </button>
        </div>
    );
};

const App = () => {
    const [todos, setTodos] = useState([]);

    useEffect(() => {
        fetchTodos();
    }, []);

    const fetchTodos = () => {
        axios
            .get('http://localhost:3001/api/items/get')
            .then((res) => setTodos(res.data.items))
            .catch((error) => {
                console.error('Error fetching todos:', error);
            });
    };

    const addTodo = (newTodo) => {
        setTodos([...todos, newTodo]);
        fetchTodos();
    };

    const deleteTodo = (id) => {
        axios
            .delete(`http://localhost:3001/api/items/del/${id}`)
            .then(() => {
                setTodos(todos.filter((todo) => todo.id !== id));
            })
            .catch((error) => {
                console.error('Error deleting task:', error);
            });
    };

    const toggleCompleteTodo = (id, completed) => {
        if (completed) {
            console.log("Невозможно изменить статус выполненной задачи");
            return;
        }

        const toggleCompleteTodo = (id, completed) => {
            const updatedTodos = todos.map((todo) => {
                if (todo.id === id) {
                    return { ...todo, completed };
                }
                return todo;
            });

            setTodos(updatedTodos);

            if (completed) {
                const completedTodo = todos.find((todo) => todo.id === id);
                setCompletedTodos((prevCompletedTodos) => [...prevCompletedTodos, completedTodo]);
            } else {
                setCompletedTodos((prevCompletedTodos) =>
                    prevCompletedTodos.filter((todo) => todo.id !== id)
                );
            }

            axios
                .put(`http://localhost:3001/api/items/complete/${id}`, { completed })
                .then(() => {
                    console.log(`Задача ${id} обновлена`);
                })
                .catch((error) => {
                    console.error('Ошибка при обновлении задачи:', error);
                });
        };
    };


    return (
        <div style={{ fontFamily: 'Roboto, sans-serif', fontSize: '1.5rem' }}>
            <h1>Список задач</h1>
            <AddTodo onAddTodo={addTodo} />
            <TodoList todos={todos} onDelete={deleteTodo} onToggleComplete={toggleCompleteTodo} />
        </div>
    );
};

export default App;
