import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTrash } from '@fortawesome/free-solid-svg-icons';

const TodoList = () => {
    const [todos, setTodos] = useState([]); // Используйте todos вместо items

    useEffect(() => {
        fetchTodos(); // Вызывайте функцию загрузки задач при монтировании компонента
    }, []);

    const fetchTodos = () => {
        axios
            .get('http://localhost:3001/api/items/get')
            .then((res) => setTodos(res.data.items))
            .catch((error) => {
                console.error('Error fetching todos:', error);
            });
    };

    const handleDelete = (id) => {
        axios
            .delete(`http://localhost:3001/api/items/del/${id}`)
            .then(() => {
                setTodos((prevTodos) => prevTodos.filter((todo) => todo.id !== id));
            })
            .catch((error) => {
                console.error('Error deleting task:', error);
            });
    }

    if (todos.length === 0) return <div>Loading...</div>;

    return (
        <div>
            {todos.map((todo) => (
                <div key={todo.id} style={{ display: 'flex', alignItems: 'center', padding: '5px 0px 5px 0px' }}>
                    <FontAwesomeIcon
                        icon={faTrash}
                        style={{ cursor: 'pointer', border: '1px solid #ccc', padding: '4px' }}
                        onClick={() => handleDelete(todo.id)}
                    />
                    <span style={{ marginLeft: '10px' }}>{todo.name}</span>
                </div>
            ))}
        </div>
    );
}

export default TodoList;
