import React, { useState, useEffect } from 'react';
import axios from 'axios';
import TodoList from './components/TodoList';
import AddTodo from './components/AddTodo';

const App = () => {
    const [todos, setTodos] = useState([]);

    useEffect(() => {
        fetchTodos();
    }, []);

    const fetchTodos = () => {
        axios
            .get('http://localhost:3001/api/items/get')
            .then((res) => setTodos(res.data.items))
            .catch((error) => {
                console.error('Error fetching todos:', error);
            });
    };

    const addTodo = (newTodo) => {
        fetchTodos();
        setTodos([...todos, newTodo]);
        fetchTodos(); // Обновить список задач после добавления
    };

    const deleteTodo = (id) => {
        axios
            .delete(`http://localhost:3001/api/items/del/${id}`)
            .then(() => {
                setTodos(todos.filter((todo) => todo.id !== id));
            })
            .catch((error) => {
                console.error('Error deleting task:', error);
            });
    };

    return (
        <div style={{ fontFamily: 'Roboto, sans-serif', fontSize: '1.5rem' }}>
            <h1>Список задач</h1>
            <AddTodo onAddTodo={addTodo} />
            <TodoList todos={todos} onDelete={deleteTodo} />
        </div>
    );
};

export default App;
