import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import MessageList from './MessageList';
import MessageInput from './MessageInput';

const Chat = () => {
    const [socket, setSocket] = useState(null);
    const [nickname, setNickname] = useState('');
    const [messages, setMessages] = useState([]);

    useEffect(() => {
        const newSocket = io('ws://localhost:8080'); // Подставьте ваш URL сервера

        newSocket.on('connect', () => {
            newSocket.emit('setNickname', nickname); // Отправляем никнейм на сервер
        });

        setSocket(newSocket);

        return () => newSocket.close(); // Закрываем соединение при размонтировании компонента
    }, [nickname]);

    const sendMessage = (message) => {
        if (socket) {
            socket.emit('sendMessage', { message, nickname });
        }
    };

    useEffect(() => {
        if (socket) {
            socket.on('receiveMessage', (message) => {
                setMessages((prevMessages) => [...prevMessages, message]);
            });
        }
    }, [socket]);

    return (
        <div>
            <MessageList messages={messages} />
            <MessageInput sendMessage={sendMessage} setNickname={setNickname} />
        </div>
    );
};

export default Chat;
