import React, { useEffect, useState, useMemo } from 'react';
import { io } from 'socket.io-client';
import { Link } from 'react-router-dom';

const Chat = () => {
    const [messages, setMessages] = useState([]);
    const [newMessage, setNewMessage] = useState('');

    const socket = useMemo(() => {
        // Инициализация socket в useMemo
        // Вам нужно выбрать один из вариантов инициализации (WebSocket или socket.io)
        // WebSocket:
        return new WebSocket("ws://localhost:8080");
        // socket.io:
        // return io('ws://localhost:8080');
    }, []);

    useEffect(() => {
        socket.onmessage = (event) => {
            setMessages((prevMessages) => [...prevMessages, event.data]);
        };

        return () => {
            socket.close();
        };
    }, [socket]);

    const handleSendMessage = () => {
        if (newMessage.trim() === '') {
            return;
        }

        socket.send(newMessage);
        setNewMessage('');
    };

    return (
        <div style={{ fontFamily: 'Roboto, sans-serif', fontSize: '1.5rem' }}>
            <Link to="/">Перейти к задачам</Link>
            <div>
                {messages.map((message, index) => (
                    <div key={index}>Ответ: {message}</div>
                ))}
            </div>
            <input
                type="text"
                value={newMessage}
                onChange={(e) => setNewMessage(e.target.value)}
            />
            <button onClick={handleSendMessage}>Отправить</button>
        </div>
    );
};

export default Chat;
