import React, { useState, useEffect } from 'react';
import axios from 'axios';

const TodoList = () => {
    const [items, setItems] = useState(null);

    useEffect(() => {
        axios
            .get('http://localhost:3001/api/items/get')
            .then((res) => setItems(res.data.items))
    }, []);

    const handleDelete = (id) => {
        axios
            .delete(`http://localhost:3001/api/items/del/${id}`)
            .then(() => {
                // Обновите список задач после удаления.
                setItems((prevItems) => prevItems.filter((item) => item.id !== id));
            })
            .catch((error) => {
                console.error('Error deleting task:', error);
            });
    }

    if (items === null) return <div>Loading...</div>;

    return (
        <div>
            {items.map((item) => (
                <div key={item.id}>
                    <button style={{ margin: '20px 5px 0px 0px' }} onClick={() => handleDelete(item.id)}>Удалить</button>
                    <span style={{ margin: '0px' }}>{item.name}</span>
                </div>
            ))}
        </div>
    );
}

export default TodoList;
