import React, { useEffect, useState } from 'react';
import { io } from 'socket.io-client';

const Chat = () => {
    const [messages, setMessages] = useState([]);
    const [newMessage, setNewMessage] = useState('');
    const socket = io('ws://localhost:8080'); // Установка соединения с WebSocket-сервером

    useEffect(() => {
        // Обработчик сообщений от сервера
        socket.on('message', (message) => {
            // Обработка полученного сообщения от сервера
            setMessages((prevMessages) => [...prevMessages, message]);
        });

        return () => {
            socket.close(); // Закрытие соединения при размонтировании компонента
        };
    }, [socket]);

    const handleSendMessage = () => {
        socket.emit('message', newMessage); // Отправка сообщения на сервер
        setNewMessage(''); // Сброс поля ввода сообщения
    };

    return (
        <div>
            <div>
                {messages.map((message, index) => (
                    <div key={index}>{message}</div>
                ))}
            </div>
            <input
                type="text"
                value={newMessage}
                onChange={(e) => setNewMessage(e.target.value)}
            />
            <button onClick={handleSendMessage}>Отправить</button>
        </div>
    );
};

export default Chat;
