import React, { useState } from 'react';

const AddTodo = () => {
    const [newTodo, setNewTodo] = useState('');

    const handleAddTodo = () => {
        fetch('http://localhost:3001/api/items/add', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                name: newTodo,
                completed: false, // Можно указать начальное значение
            }),
        })
            .then(response => response.json())
            .then(data => {
                console.log('New todo added:', data);
                // Дополнительный код для обновления списка заметок, если необходимо.
            })
            .catch(error => {
                console.error('Error adding new todo:', error);
            });
    }

    return (
        <div>
            <input
                type="text"
                placeholder="Введите новую заметку"
                value={newTodo}
                onChange={(e) => setNewTodo(e.target.value)}
            />
            <button onClick={handleAddTodo}>Добавить заметку</button>
        </div>
    );
};

export default AddTodo;
