import React, { useEffect } from 'react';
import { io } from 'socket.io-client';

const Chat = () => {
    useEffect(() => {
        const socket = io('http://localhost:8080');

        // Обработчик сообщений от сервера
        socket.on('message', (message) => {
            // Обработка полученного сообщения от сервера
            console.log('Получено сообщение от сервера:', message);
        });

        // Отправка сообщения на сервер
        socket.emit('message', 'Привет от клиента WebSocket!');

        // Функция для закрытия соединения при размонтировании компонента
        return () => {
            socket.close();
        };
    }, []);

    return <div>WebSocket работает</div>;
};

export default Chat;
